INSERT INTO `lei_ordinaria` VALUES (28,'null',3,3,0,0,'Dispõe sobre a manutenção da boa qualidade do ar em estacionamentos localizados em ambientes fechados, conforme especifica, e dá outras providências.'),(29,'null',0,0,0,0,'Obriga as empresas de cartões de crédito ou débito a avisar aos consumidores deste Estado acerca do bloqueio do cartão e dá outras providências.'),(40,'null',0,0,0,0,'Institui, no Calendário Oficial de Eventos do Estado de Pernambuco, a Festa de Santos Reis, no Município de Carpina.'),(41,'null',0,0,0,0,'Dispõe sobre os prazos na marcação de consultas e exames para pessoas com mais de 60 anos nas Unidades de Saúde Públicas pertencentes ao Estado de Pernambuco.'),(42,'null',0,0,0,0,'Dispõe sobre o direito à liberação do serviço dos Policiais Militares, Bombeiros Militares e Policiais Civis, no âmbito do Estado de Pernambuco, para frequentar aulas quando regularmente matriculados em Curso Superior.'),(43,'null',0,0,0,0,'Dispõe sobre a proibição de homenagens no Estado de Pernambuco a pessoas que tenham sido condenadas por atos de improbidade e corrupção, e dá outras providências.'),(44,'null',0,0,0,0,'Dispõe sobre a obrigatoriedade das concessionárias automotivas sediadas no Estado de Pernambuco plantarem uma árvore por automóvel zero km vendido.'),(45,'null',0,0,0,0,'Institui, no Calendário de Eventos do Estado de Pernambuco, a Semana Estadual da Imigração. '),(46,'null',0,0,0,0,'Dispõe sobre a proteção no atendimento de pessoas com deficiência nos serviços de saúde pública do Estado de Pernambuco, e dá outras providências.'),(47,'null',0,0,0,0,'Institui, no Calendário de Eventos do Estado de Pernambuco, o Dia Estadual do Karatê, e dá outras providências.'),(48,'null',0,0,0,0,'Determina que as maternidades públicas e privadas no Estado de Pernambuco garantam o treinamento para socorro em caso de engasgamento e prevenção de morte súbita, destinados aos pais ou responsáveis por recém-nascidos e dá outras providências. '),(49,'null',0,2,0,0,'Dispõe sobre a oferta de Esporte, como atividade educacional complementar, independente de Educação Física obrigatória, a todos os alunos do ensino médio das escolas públicas estaduais e dá outras providências.'),(50,'null',0,0,0,0,'Dispõe sobre a obrigatoriedade da reserva de mesas e cadeiras para idosos e gestantes nas praças de alimentação dos shoppings  centers, no Estado de Pernambuco e dá outras providências.'),(51,'null',0,0,0,0,'Institui Impressão no Sistema Braille para contas de fornecimento e serviço público de telefone, energia elétrica, água, para usuários portadores de deficiência visual e, dá outras providências.'),(52,'null',0,0,0,0,'Ementa: Dispõe sobre a inclusão do tipo sanguíneo na carteira de habilitação emitida pelo órgão estadual de trânsito, e dá outras providências.'),(53,'null',2,4,0,0,'Dispõe sobre a Obrigatoriedade aos responsáveis legais pelos estádios e campos de futebol a fixar placa em local de fácil visibilidade, com os dizeres Diga não ao Racismo nos Estádios do Estado de Pernambuco.'),(54,'null',0,0,0,0,'Dispõe sobre o mecanismo de inibição da violência contra mulher no Estado de Pernambuco, através de multa contra o agressor, em caso de utilização de serviços públicos.'),(55,'null',0,0,0,0,'Institui o Dia Estadual do Conciliador de Justiça.'),(56,'null',0,2,0,0,'Institui o Dia Estadual Do Motoboy e Mototaxista.'),(57,'null',0,0,0,0,'Institui aplicação de multas às construtoras e as incorporadoras por atraso na entrega do imóvel ao comprador.'),(58,'null',0,0,0,0,'Institui as Construtoras fixar Placa indicativa com  os seus respectivos nomes e números dos profissionais habilitados no     CREA, antes do Habite-se.  '),(59,'null',0,0,0,0,'Institui Reserva de Imóveis Populares para Idosos.'),(60,'null',0,0,0,0,'Institui o Dia Estadual de Conscientização e Combate a Anemia Falciforme em Pernambuco.'),(61,'null',0,0,0,0,'Institui o Dia Estadual do Obreiro, a ser comemorado anualmente no 3º domingo do mês de agosto, no Estado de      Pern